sys_variable_value.value:
(function execute(inputs, outputs) {

var levels = (!gs.nil(inputs.levels) && JSON.stringify(inputs.levels) != '[""]') ? inputs.levels : "";


var utils = new x_61049_unified_1.unifiedCatalogApproval(inputs.itemSysId);
var result = utils.generateApprovals(inputs.requestGR, inputs.requestedItemGR, levels);

outputs.approvals = result.approvals;
outputs.status = result.status;
outputs.message = result.message;

})(inputs, outputs);

